
public class Divison {

	public static void main(String[] args) {
		
		System.out.println("The Value of 12/2 is:" + 12/2);
		System.out.println("The Value of 12.0/2.0 is:" + 12.0/2.0);
		System.out.println("The Value of 10/4 is:" + 10/4);
		System.out.println("The Value of 10/4.0 is:" + 10/4.0);
		System.out.println("The Value of 4/10 is:" + 4/10);
		System.out.println("The Value of 4.0/10 is:" + 4.0/10);
		System.out.println("The Value of 12%3 is:" + 12%3);
		System.out.println("The Value of 10%3 is:" + 10%3);
		System.out.println("The Value of 3%10 is:" + 3%10);
		// TODO Auto-generated method stub

	}

}
